import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Nav = () => {
  const location = useLocation();
  const [activeLink, setActiveLink] = useState(location.pathname);

  useEffect(() => {
    setActiveLink(location.pathname);
  }, [location.pathname]);

  const handleLinkClick = (link) => {
    setActiveLink(link);
  };

  return (
    <nav id="nav">
      <ul>
        <li>
          <Link to="/" className={`nav-link ${activeLink === '/' ? 'active' : ''}`} onClick={() => handleLinkClick('/')}>
            <span>Home</span>
            <div className="liquid"></div>
          </Link>
        </li>
        <li>
          <Link to="/portfolio" className={`nav-link ${activeLink === '/portfolio' ? 'active' : ''}`} onClick={() => handleLinkClick('/portfolio')}>
            <span>Portfolio</span>
            <div className="liquid"></div>
          </Link>
        </li>
        <li>
          <Link to="/contact" className={`nav-link ${activeLink === '/contact' ? 'active' : ''}`} onClick={() => handleLinkClick('/contact')}>
            <span>Contact</span>
            <div className="liquid"></div>
          </Link>
        </li>
      </ul>
    </nav>
  );
};

export default Nav;
