// CmdWindow.js
import React, { useState, useEffect } from 'react';

const CmdWindow = () => {
  const [output, setOutput] = useState([]);
  const [input, setInput] = useState('');

  useEffect(() => {
    // Initialize the output with a welcome message
    setOutput(['Welcome to the CMD simulator!']);
  }, []);

  const handleInput = (event) => {
    const userInput = event.target.value;
    setInput(userInput);
    // Simulate the execution of the command
    simulateCommand(userInput);
  };

  const simulateCommand = (command) => {
    if (command === 'scrape') {
      // Simulate the scraping process
      const scrapingMessages = [
        'Scraping page 1...',
        'Scraping page 2...',
        'Scraping page 3...',
        'Done scraping!',
      ];

      scrapingMessages.forEach((message, index) => {
        setTimeout(() => {
          setOutput((prevOutput) => [...prevOutput, message]);
        }, index * 1000); // delay each message by 1 second
      });
    } else {
      // Handle other commands
      setOutput((prevOutput) => [...prevOutput, `Unknown command: ${command}`]);
    }
  };

  return (
    <div className="cmd-window">
      <div className="cmd-output">
        {output.map((line, index) => (
          <div key={index}>{line}</div>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={handleInput}
        placeholder="Enter a command..."
      />
    </div>
  );
};

export default CmdWindow;
