import React, { useState } from 'react';
import './Services.css'; // Import the CSS for services layout
import ServiceModal from './ServiceModal'; // Import the pop-up modal component

const Services = () => {
  const [selectedService, setSelectedService] = useState(null);

  const services = [
    {
      id: 1,
      title: 'Scraping Reddit',
      description: 'Extract Reddit posts and comments based on your criteria.',
    },
    {
      id: 2,
      title: 'E-commerce Price Monitoring',
      description: 'Scrape and monitor prices from e-commerce websites.',
    },
    {
      id: 3,
      title: 'Social Media Data Extraction',
      description: 'Extract posts and user data from various social media platforms.',
    }
  ];

  const handleOrder = (service) => {
    setSelectedService(service);
  };

  const closeModal = () => {
    setSelectedService(null);
  };

  return (
    <div className="services-section">
      <h2>Our Web Scraping Services</h2>
      <div className="services-container">
        {services.map((service) => (
          <div key={service.id} className="service-card">
            <h3>{service.title}</h3>
            <p>{service.description}</p>
            <button onClick={() => handleOrder(service)}>Order Service</button>
          </div>
        ))}
      </div>

      {selectedService && (
        <ServiceModal service={selectedService} onClose={closeModal} />
      )}
    </div>
  );
};

export default Services;
