import React from 'react';
import './Review.css';

const Review = ({ review }) => {
  const rating = review.rating;
  const wholeStars = Math.floor(rating);
  const fractionalStar = rating - wholeStars;

  return (
    <div className='review'>
      <h3>{review.title}</h3>
      <p>{review.description}</p>
      <p>
        Rating: 
        {[...Array(wholeStars)].map((_, i) => (
          <span key={i} className="star">&#x2605;</span> // whole star
        ))}
        {fractionalStar > 0 && (
          <span className="star partial">
            <span
              style={{
                width: `${fractionalStar * 100}%`,
                overflow: 'hidden',
                display: 'inline-block',
              }}
            >
              &#x2605;
            </span>
          </span>
        )}
        {[...Array(5 - wholeStars - (fractionalStar > 0 ? 1 : 0))].map((_, i) => (
          <span key={i} className="star">&#x2606;</span> // empty star
        ))}
        {rating}/5
      </p>
    </div>
  );
};

export default Review;
